    /**
     * 方法一：使用set
     */
    public int minimumLengthEncoding(String[] words) {
        if (words == null || words.length == 0) {
            return 0;
        }

        Set<String> set = new HashSet<>();
        for (String word : words) {
            set.add(word);
        }

        List<String> list = new ArrayList<>(set);
        list.forEach(word -> {
            int len = word.length();
            if (len != 1) {
                char[] chars = word.toCharArray();
                for (int offset = chars.length - 1; offset > 0; offset--) {
                    String shortWord = String.valueOf(chars, offset, len - offset);
                    if (set.contains(shortWord)) {
                        set.remove(shortWord);
                    }
                }
            }
        });

        return sumLength(set);
    }

    private int sumLength(Set<String> set) {
        int sum = 0;
        for (String s : set) {
            sum = sum + s.length() + 1;
        }
        return sum;
    }
